class Group{

    constructor(){
        this.list = []
        this.objects = new Map()
        this.selectedObject
    }

    add(obj){
        this.list.push(obj.id)
        this.objects.set(obj.id, obj)
    }

    selectObject(id){
        this.selectedObject = this.objects.get(id)
        return this.selectedObject
    }

    deleteFromList(id){
        for(var i = 0; i<this.list.length; i++){
            if(this.list[i] == id){
                this.list.splice(i,1)
                return this.list
            }
        }
    }

    export(){
        var arr = []

        this.objects.forEach(function(obj,key,map){
            if(!obj.meta.disabled){
                arr.push(obj.export())
            }
        })

        return arr
    }

}

class Team extends Group{

    constructor(id, name, ai){
        super()

        this.id = id
        this.name = name 
        this.ai = ai
    }

    setName(name){
        this.name = name
        return name
    }

    toggleAI(set){

        if(set !== undefined){
            if(set){
                this.ai = true
            }else{
                this.ai = false
            }
        }else{
            this.ai = !this.ai
        }

        if(this.ai){
            doAIsequence()
        }

        return this.ai
    }

}

