class Entity{

    constructor(){
        this.flipped = 1
    }

    /**
     * Flips the sprite of the piece
     * @param {int} direction 
     */
    flip(direction){

        if(direction === undefined){

            if(this.flipped == 1){
                this.flipped = -1
            }else{
                this.flipped = 1
            }

        }else{
            console.log(direction)

            if(Math.abs(direction) > 90){
                this.flipped = -1
            }else{
                this.flipped = 1
            }

        }

        this.sprite.style.transform = 'scaleX('+this.flipped+')';
        
        return this.flipped
    }

}

